/** * class represents a node in a linked priority queue  * @author Yijun He */public class PriorityNode<E>{   /*    * reference to next node    */   private PriorityNode<E> next;      /*    * reference to the element in the node    */   private E element;      /*    * priority of the node    */   private double priority;      /*    * create an empty node    */   public PriorityNode(){	  next = null;	  element = null;	  priority = 0;   }      /*    * create an node storing element and priority    */   public PriorityNode(E elem, double p){	   next = null;	   element = elem;	   priority = p;   }      /*    * create an node storing element     */   public PriorityNode(E elem){	   next= null;	   element = elem;	   priority = 0;   }      /*    * return the priority stored in this node    */   public double getPriority(){	   return priority;   }      /*    * sets the node's priority     */   public void setPriority(double elem){	   priority = elem;   }      /*    * return the next node     */   public PriorityNode<E> getNext(){	   return next;   }      /*    * set the node that follows this one    */   public void setNext(PriorityNode<E> node){	   next = node;   }      /*    * return the element stored in this node    */   public E getElement(){	   return element;   }      /*    * set the element for this node    */   public void setElement(E elem){	   element = elem;   }         }