/** * ?? * * @author  * @author  * @version */public class PriorityNode<E>{   private PriorityNode<E> next;   private E element;   private double priority;      public PriorityNode(){	  next = null;	  element = null;   }      public PriorityNode(E elem, double p){	   next = null;	   element = elem;	   priority = p;   }   public PriorityNode(E elem){	   next= null;	   element = elem;	   priority = 0;   }      public double getPriority(){	   return priority;   }      public void setPriority( double elem){	   priority = elem;   }      public PriorityNode<E> getNext(){	   return next;   }      public void setNext(PriorityNode<E> node){	   next = node;   }      public E getElement(){	   return element;   }      public void setElement(E elem){	   element = elem;   }         }